delete(instrfind);
SerialHandler = serial('COM3');
set(SerialHandler,'BaudRate',128000);
InputBufferSize = 1000;
OutputBufferSize = 500000;
xLabels = [0,650, 1300, 1950, 2600, 3250, 3900, 4550, 5200, 5850, 6500]; %Números del eje x al plotear la fase
BufferOutput = zeros(1,OutputBufferSize);
SerialHandler.InputBufferSize = InputBufferSize;
SerialHandler.OutputBufferSize = OutputBufferSize;
SerialHandler.Timeout = 1000;
fopen(SerialHandler);
while(1)
    Max = 1000;
    Min = 1;
    for x = 1:1:500 %Se reciben 1000 valores 500 veces
        z = 1;
        BufferSonido = fread(SerialHandler, InputBufferSize, 'uint8'); %Se leen por UART 1000 bytes desde el micro
        Transformada = fft(BufferSonido).'; %Matriz transpuesta
        Transformada(1) = 0;
        Transformada(2) = 0;
        
        if(FilterSelect == 0) %Pasa bajas
            SenalFiltrada = Transformada .* fft(HPasaBaja);
            BufferSonido = real(round(ifft(SenalFiltrada)))/500;
            BufferSonido = BufferSonido + min(BufferSonido)*-1;
        elseif (FilterSelect == 1) %Pasa altas
            SenalFiltrada = Transformada .* fft(HPasaAlta);
            BufferSonido = real(round(ifft(SenalFiltrada)))/150;
            BufferSonido = BufferSonido + min(BufferSonido)*-1;
        elseif(FilterSelect == 2) %Pasa banda
            SenalFiltrada = Transformada .* fft(HPasaBanda);
            BufferSonido = real(round(ifft(SenalFiltrada)))/40;
            BufferSonido = BufferSonido + min(BufferSonido)*-1;
        else %Pasa todo
            SenalFiltrada  = Transformada;
        end
        
        FaseOriginal = Transformada.*conj(Transformada)/InputBufferSize + 1;
        FaseFiltrada = SenalFiltrada.*conj(SenalFiltrada)/InputBufferSize + 1;
        
        for y = Min:1:Max
            BufferOutput(y) = BufferSonido(z);
            z = z+1;
        end
        Max = Max + 1000;
        Min = Min + 1000;
    end
    hold on
    subplot(2,1,1);
    plot(FaseOriginal(1:InputBufferSize/2));
    set(gca, 'XTickLabel', xLabels); %Se cambian los números del eje x por los del arreglo
    xlabel('Frecuencia(Hz)')
    subplot(2,1,2);
    plot(FaseFiltrada(1:InputBufferSize/2));
    set(gca, 'XTickLabel', xLabels); %Se cambian los números del eje x por los del arreglo
    xlabel('Frecuencia(Hz)')
    drawnow
    fwrite(SerialHandler, BufferOutput, 'uint8');
    
end
fopen(SerialHandler);


